import { PayPalSDK } from '@paypal/paypal-server-sdk';

/**
 * PayPal SDK configuration
 * 
 * This file sets up the PayPal client with the appropriate environment
 * based on the PAYPAL_ENV environment variable.
 */

// Create PayPal client
const paypalClient = new PayPalSDK({
  clientId: process.env.PAYPAL_CLIENT_ID || '',
  clientSecret: process.env.PAYPAL_CLIENT_SECRET || '',
  environment: process.env.PAYPAL_ENV === 'live' ? 'live' : 'sandbox',
});

/**
 * Create a PayPal order
 * 
 * @param amount - The amount to charge
 * @param currency - The currency code (e.g., 'USD')
 * @param description - Description of the purchase
 * @returns Promise resolving to the order ID
 */
export const createPayPalOrder = async (
  amount: string,
  currency: string = 'USD',
  description: string = 'BillingSDK Purchase'
) => {
  try {
    const order = await paypalClient.createOrder({
      intent: 'CAPTURE',
      purchase_units: [
        {
          description,
          amount: {
            currency_code: currency,
            value: amount,
          },
        },
      ],
      application_context: {
        cancel_url: `${process.env.NEXT_PUBLIC_APP_URL}/cancel`,
        return_url: `${process.env.NEXT_PUBLIC_APP_URL}/return`,
      },
    });

    return order.id;
  } catch (error) {
    console.error('Error creating PayPal order:', error);
    throw error;
  }
};

/**
 * Capture a PayPal order
 * 
 * @param orderId - The PayPal order ID to capture
 * @returns Promise resolving to the capture result
 */
export const capturePayPalOrder = async (orderId: string) => {
  try {
    const capture = await paypalClient.capturePaymentForOrder(orderId);
    return capture;
  } catch (error) {
    console.error('Error capturing PayPal order:', error);
    throw error;
  }
};

/**
 * Get order details
 * 
 * @param orderId - The PayPal order ID to retrieve
 * @returns Promise resolving to the order details
 */
export const getPayPalOrder = async (orderId: string) => {
  try {
    const order = await paypalClient.getOrder(orderId);
    return order;
  } catch (error) {
    console.error('Error retrieving PayPal order:', error);
    throw error;
  }
};

import { useState } from 'react';

/**
 * Custom React hook for managing PayPal integration
 * 
 * This hook provides functions and state management for integrating
 * PayPal payments into your React application.
 */

interface PayPalOrder {
  id: string;
  status: string;
  amount: string;
  currency: string;
}

export const usePayPal = () => {
  const [order, setOrder] = useState<PayPalOrder | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  /**
   * Create a PayPal order
   * 
   * @param amount - The amount to charge
   * @param currency - The currency code (e.g., 'USD')
   * @param description - Description of the purchase
   */
  const createOrder = async (
    amount: string,
    currency: string = 'USD',
    description: string = 'BillingSDK Purchase'
  ) => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch('/api/(paypal)/checkout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ amount, currency, description }),
      });

      if (!response.ok) {
        throw new Error('Failed to create PayPal order');
      }

      const data = await response.json();
      setOrder({
        id: data.id,
        status: 'CREATED',
        amount,
        currency,
      });

      return data.id;
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Unknown error';
      setError(errorMessage);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  /**
   * Capture a PayPal order
   * 
   * @param orderId - The PayPal order ID to capture
   */
  const captureOrder = async (orderId: string) => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`/api/(paypal)/order/${orderId}/capture`, {
        method: 'POST',
      });

      if (!response.ok) {
        throw new Error('Failed to capture PayPal order');
      }

      const data = await response.json();
      setOrder(prev => prev ? { ...prev, status: 'CAPTURED' } : null);

      return data;
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Unknown error';
      setError(errorMessage);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  /**
   * Reset the hook state
   */
  const reset = () => {
    setOrder(null);
    setLoading(false);
    setError(null);
  };

  return {
    order,
    loading,
    error,
    createOrder,
    captureOrder,
    reset,
  };
};

import { NextRequest } from 'next/server';
import { createPayPalOrder } from '@/lib/paypal';

/**
 * Create a PayPal order for checkout
 * POST /api/(paypal)/checkout
 * 
 * This endpoint is called by the frontend to create a PayPal order
 * before redirecting the user to PayPal for approval.
 * 
 * Request body:
 * - amount: string (required)
 * - currency: string (optional, defaults to 'USD')
 * - description: string (optional)
 */
export async function POST(req: NextRequest) {
  try {
    const { amount, currency = 'USD', description = 'BillingSDK Purchase' } = await req.json();

    if (!amount) {
      return new Response(
        JSON.stringify({ error: 'Amount is required' }),
        { status: 400, headers: { 'Content-Type': 'application/json' } }
      );
    }

    const orderId = await createPayPalOrder(amount, currency, description);

    return new Response(
      JSON.stringify({ id: orderId }),
      { status: 200, headers: { 'Content-Type': 'application/json' } }
    );
  } catch (error) {
    console.error('Error creating PayPal checkout order:', error);

    return new Response(
      JSON.stringify({ error: 'Failed to create PayPal checkout order' }),
      { status: 500, headers: { 'Content-Type': 'application/json' } }
    );
  }
}

import { NextRequest } from 'next/server';
import { getPayPalOrder } from '@/lib/paypal';

/**
 * Get PayPal order details
 * GET /api/(paypal)/order/[orderId]
 * 
 * Path parameters:
 * - orderId: string (required)
 */
export async function GET(
  req: NextRequest,
  { params }: { params: { orderId: string } }
) {
  try {
    const { orderId } = params;

    if (!orderId) {
      return new Response(
        JSON.stringify({ error: 'Order ID is required' }),
        { status: 400, headers: { 'Content-Type': 'application/json' } }
      );
    }

    const order = await getPayPalOrder(orderId);

    return new Response(
      JSON.stringify(order),
      { status: 200, headers: { 'Content-Type': 'application/json' } }
    );
  } catch (error) {
    console.error('Error retrieving PayPal order:', error);

    return new Response(
      JSON.stringify({ error: 'Failed to retrieve PayPal order' }),
      { status: 500, headers: { 'Content-Type': 'application/json' } }
    );
  }
}

import { NextRequest } from 'next/server';
import { capturePayPalOrder } from '@/lib/paypal';

/**
 * Capture a PayPal order
 * POST /api/(paypal)/order/capture
 * 
 * Request body:
 * - orderId: string (required)
 */
export async function POST(req: NextRequest) {
  try {
    const { orderId } = await req.json();

    if (!orderId) {
      return new Response(
        JSON.stringify({ error: 'Order ID is required' }),
        { status: 400, headers: { 'Content-Type': 'application/json' } }
      );
    }

    const capture = await capturePayPalOrder(orderId);

    return new Response(
      JSON.stringify(capture),
      { status: 200, headers: { 'Content-Type': 'application/json' } }
    );
  } catch (error) {
    console.error('Error capturing PayPal order:', error);

    return new Response(
      JSON.stringify({ error: 'Failed to capture PayPal order' }),
      { status: 500, headers: { 'Content-Type': 'application/json' } }
    );
  }
}

import { NextRequest } from 'next/server';
import { Webhook } from 'standardwebhooks';

/**
 * Handle PayPal webhook events
 * POST /api/(paypal)/webhook
 * 
 * This endpoint receives webhook events from PayPal and processes them accordingly.
 * The request body contains the event data, and the headers contain the signature
 * for verification.
 */
export async function POST(req: NextRequest) {
  try {
    // Initialize webhook handler with PayPal webhook ID from environment variables
    const webhook = new Webhook(process.env.PAYPAL_WEBHOOK_ID || '');

    // Get the request body and headers
    const body = await req.text();
    const headers = Object.fromEntries(req.headers);

    // Verify the webhook signature
    const verified = webhook.verify(body, headers);

    if (!verified) {
      return new Response(
        JSON.stringify({ error: 'Invalid webhook signature' }),
        { status: 400, headers: { 'Content-Type': 'application/json' } }
      );
    }

    // Parse the event data
    const event = JSON.parse(body);
    const eventType = event.event_type;

    switch (eventType) {
      case 'PAYMENT.CAPTURE.COMPLETED':
        // Handle successful payment capture
        console.log('Payment captured:', event);
        // Update your database, send confirmation emails, etc.
        break;

      case 'PAYMENT.CAPTURE.REFUNDED':
        // Handle payment refund
        console.log('Payment refunded:', event);
        // Update your database, notify user, etc.
        break;

      case 'BILLING.SUBSCRIPTION.ACTIVATED':
        // Handle subscription activation
        console.log('Subscription activated:', event);
        // Update your database, grant access to premium features, etc.
        break;

      case 'BILLING.SUBSCRIPTION.CANCELLED':
        // Handle subscription cancellation
        console.log('Subscription cancelled:', event);
        // Update your database, revoke access to premium features, etc.
        break;

      default:
        console.log('Unhandled PayPal event:', eventType);
        break;
    }

    return new Response(
      JSON.stringify({ received: true }),
      { status: 200, headers: { 'Content-Type': 'application/json' } }
    );
  } catch (error) {
    console.error('Error processing PayPal webhook:', error);

    return new Response(
      JSON.stringify({ error: 'Failed to process webhook' }),
      { status: 500, headers: { 'Content-Type': 'application/json' } }
    );
  }
}
